import logo from './logo.svg';
import './App.css';
import React from "react"
import {nanoid} from "nanoid"
import Confetti from "react-confetti"
import Die from "./Die"
import Tictactoe from "./Tictactoe"


function App() {
  const [dice, setDice] = React.useState(allNewDice())
  const [tenzies, setTenzies] = React.useState(false)
  const [numbers,setNumbers] = React.useState(allNewNumbers())
  const [turn, setTurn] = React.useState("X")

  // TTT function
  function nextTurn() {
    setTurn(prevValue => prevValue=="X" ? "Y" : "X")
  }

  function generateNewDie() {
    return {
        value: Math.ceil(Math.random() * 6),
        isHeld: false,
        id: nanoid()
    }
  }   
  function allNewDice() {
    const newDice = []
    for (let i = 0; i < 10; i++) {         // Reduce the # here for testing purpose
        newDice.push(generateNewDie())
    }
    return newDice
  }

  //TTT function
  function generateNewNumber() {
    return {
        value: Math.ceil(Math.random() * 9),
        // isHeld: false,
        id: nanoid()
    }
  }   

  //TTT function
  function allNewNumbers() {
    const newNumbers=[]

    for (let i=0; i<9; i++){
      newNumbers.push(generateNewNumber())
    }
    return newNumbers
  }


  function rollDice() {
    if(!tenzies) {
        setDice(oldDice => oldDice.map(die => {
            return die.isHeld ? 
                die :
                generateNewDie()
        }))
    } else {
        setTenzies(false)
        setDice(allNewDice())
    }
  }

  function holdDice(id) {
    setDice(oldDice => oldDice.map(die => {
        return die.id === id ? 
            {...die, isHeld: !die.isHeld} :
            die
    }))
  }

  const diceElements = dice.map(die => (
    <Die 
        key={die.id} 
        value={die.value} 
        isHeld={die.isHeld} 
        holdDice={() => holdDice(die.id)}
    />
  ))

  // TTT function
  const numberElements = numbers.map(x => (
    <Tictactoe
      number = {x.value}
      key = {x.id}
    />
  ))



  function checkWinningState(dice){
    const num = dice[0].value
    for (let i = 0; i< dice.length; i++){
        if (dice[i].value === num && dice[i].isHeld){
            continue
        } else{
            return false
        }
    }
    return true
    }

  React.useEffect(() => {
    console.log("Dice state changed")
    setTenzies(checkWinningState(dice))
  }, [dice])



  return (
    <main className="App">
      {tenzies && <Confetti />}
      <h1 className="title">{tenzies ? "You Win!" : "Tenzies"}</h1>
      <p className="instructions">Roll until all dice are the same. 
            Click each die to freeze it at its current value between rolls.</p>
      <div className="dice-container"> 
        {diceElements}
      </div>

      <div className= "tictactoe-container">
        {/* <Tictactoe 
          numbers= {numbers}
          key = 
        /> */}
        {numberElements}
      </div>


      <button 
        className="roll-dice" 
        onClick={rollDice}
      >
        {tenzies ? "New Game" : "Roll"}
      </button>

      <button
        onClick={nextTurn}  
      >
        Switch Turns
      </button>

    </main>
  );
}

export default App;
